// Add corner positioning to matchFlowController.js:

function setupCornerDefense(team, cornerSide) {
  const goalX = team.attackingGoalX === 52.5 ? -52.5 : 52.5;
  const roles = [
    { role: 'nearpost', pos: { x: goalX, y: cornerSide * 2.5 } },
    { role: 'farpost', pos: { x: goalX, y: cornerSide * -5.0 } },
    { role: 'zonal1', pos: { x: goalX + 4, y: -2 } },
    { role: 'zonal2', pos: { x: goalX + 4, y: 2 } },
    { role: 'zonal3', pos: { x: goalX + 6, y: 0 } },
    { role: 'edge', pos: { x: goalX + 12, y: cornerSide * 8 } },
  ];
  // Assign defenders to roles by priority (CBs first)
  const defenders = team.players.filter(p => p.onPitch && p.role !== 'GK')
    .sort((a, b) => defPriority(a.role) - defPriority(b.role));
  roles.forEach((r, i) => {
    if (defenders[i]) {
      defenders[i].cornerTarget = r.pos;
      defenders[i].cornerRole = r.role;
    }
  });
}

