// Improve GK penalty logic in aiKeeper.js:
function computePenaltySaveIntent(gk, ball, rng) {
  const anticipation = gk.attributes?.anticipation ?? 50;
  const reflex = gk.attributes?.reflexes ?? 50;

  // Skill-based prediction (not just random)
  let guessWeights = [0.33, 0.34, 0.33]; // L, C, R base
  if (anticipation > 70) {
    // Better keepers bias toward corners
    guessWeights = [0.40, 0.20, 0.40];
  }

  // Reaction delay based on reflexes
  const reactionDelay = 0.3 - (reflex / 100) * 0.15; // 0.15-0.30s

  // Choose direction based on weighted random
  const roll = rng.nextFloat();
  let dir = roll < guessWeights[0] ? -1 : roll < guessWeights[0] + guessWeights[1] ? 0 : 1;

  return { dir, reactionDelay };
}

// Add goal kick specialization to matchFlowController.js or aiKeeper.js:
function computeGoalKickIntent(gk, teammates) {
  const power = 20 + (gk.attributes?.kicking ?? 50) / 5; // 20-30 m/s
  const loft = 4.0; // high ball
  // Find best target: prefer defenders/midfielders 25-40m away
  const targets = teammates.filter(t => t.role !== 'GK' && t.onPitch)
    .map(t => ({ player: t, dist: distance(gk.pos, t.pos) }))
    .filter(t => t.dist > 20 && t.dist < 45)
    .sort((a, b) => a.dist - b.dist);
  const target = targets[0]?.player || teammates[1]; // fallback
  return { target, power, loft };
}

// Add ball-zone-based positioning to tacticsController.js:
function getPositionForBallZone(player, ballPos, formation) {
  // Divide pitch into 6 zones along X axis
  const zoneX = Math.floor((ballPos.x + 52.5) / 17.5); // 0-5
  const zoneKey = Math.min(5, Math.max(0, zoneX));

  // Each formation should have 6 position variants
  const positions = formation.ballZonePositions?.[zoneKey];
  if (positions && positions[player.formationIdx]) {
    return positions[player.formationIdx];
  }
  return formation.positions[player.formationIdx]; // fallback
}

