// Add to matchMain.js, after stat collection:

function calculatePerformanceRating(player, pStats, teamStats, resultDiff) {
  let rating = 50;

  // Passing
  const passAcc = pStats.passes > 0 ? pStats.passesComplete / pStats.passes : 0;
  rating += passAcc * 7;
  rating -= (pStats.passes - (pStats.passesComplete || 0)) * 0.5;

  // Attack
  rating += (pStats.goals || 0) * 9;
  rating += (pStats.assists || 0) * 8;
  rating += (pStats.shots || 0) * 1.5;

  // Defense
  rating += (pStats.tackles || 0) * 2;

  // Discipline
  rating -= (pStats.yellow_cards || 0) * 5;
  rating -= (pStats.red_cards || 0) * 15;

  // Clean sheet (defenders/GK)
  if (teamStats.goals === 0 && ['GK','CB','LB','RB','LWB','RWB'].includes(player.role)) {
    rating += player.role === 'GK' ? 10 : 9;
  }

  // Result multiplier
  rating *= 1 + (resultDiff * 0.02);

  return Math.round(Math.max(30, Math.min(100, rating)));
}

