// Add player valuation to marketplaceService.js:
function estimatePlayerValue(player) {
  const ageMultiplier = player.age < 22 ? 1.3 : player.age > 30 ? 0.6 : 1.0;
  const rarityMultiplier = { free: 0.1, regular: 0.5, rare: 1.0, epic: 2.0, legendary: 5.0 };
