// New: api/services/cupService.js
function createCup(world, name, rounds) {
  const cup = { id: uuid(), name, rounds, currentRound: 0, fixtures: [], honors: [] };
  return cup;
}

function drawRound(cup, teams) {
  // Seed by division rank, random within seed groups
  const shuffled = teams.sort(() => Math.random() - 0.5);
  const fixtures = [];
  for (let i = 0; i < shuffled.length; i += 2) {
    fixtures.push({ home: shuffled[i], away: shuffled[i+1], round: cup.currentRound });
  }
  cup.fixtures.push(...fixtures);
  return fixtures;
}

function advanceCup(cup, results) {
  const winners = results.map(r => r.homeGoals > r.awayGoals ? r.home : r.away);
  cup.currentRound++;
  if (winners.length === 1) { cup.honors.push({ winner: winners[0], season: currentSeason }); return; }
  return drawRound(cup, winners);
}

// New: api/services/scoutService.js
function scoutPlayer(scoutCard, targetPlayer) {
  const accuracy = scoutCard.attributes.ability / 100;
  const report = {};
  for (const [stat, value] of Object.entries(targetPlayer.attributes)) {
    const noise = Math.round((1 - accuracy) * 10 * (Math.random() - 0.5));
    report[stat] = Math.max(0, Math.min(100, value + noise));
  }
  return { playerId: targetPlayer.id, report, accuracy, date: new Date() };
}

// New: api/services/pvpService.js
const lobby = new Map(); // userId -> { joinedAt, rating }

function joinLobby(userId, rating) {
  lobby.set(userId, { joinedAt: Date.now(), rating });
}

function matchmake() {
  const players = [...lobby.entries()].sort((a, b) => a[1].rating - b[1].rating);
  const matches = [];
  for (let i = 0; i + 1 < players.length; i += 2) {
    if (Math.abs(players[i][1].rating - players[i+1][1].rating) < 200) {
      matches.push({ home: players[i][0], away: players[i+1][0] });
      lobby.delete(players[i][0]);
      lobby.delete(players[i+1][0]);
    }
  }
  return matches;
}

