// Add match result email generation:
function generateMatchResultEmail(club, result) {
  const won = result.goalsFor > result.goalsAgainst;
  const diff = Math.abs(result.goalsFor - result.goalsAgainst);
  let type = won ? (diff >= 3 ? 'big_win' : 'win') : (diff >= 3 ? 'big_loss' : 'loss');
  return { type: 'match_result', subject: `${result.goalsFor}-${result.goalsAgainst} vs ${result.opponent}`, body: type };
}

// Enhance searchService.js with filters:
function advancedSearch(filters) {
  let query = players;
  if (filters.role) query = query.filter(p => p.role === filters.role);
  if (filters.minAge) query = query.filter(p => p.age >= filters.minAge);
  if (filters.rarity) query = query.filter(p => p.rarity === filters.rarity);
  if (filters.minStat) query = query.filter(p => p.stars >= filters.minStat);
  return query.sort((a, b) => b.stars - a.stars);
}

